# -*- coding: utf-8 -*-"""Spyder Editor"""'''1) For use in handling .dra files from DDBj, these are the japanese verseion of.sra files2) Downloads .dra files, then converts them to .gz files'''   import osindex_file = 'brach_dist_transcripts.idx'for item in os.listdir('./'):    if item.endswith('.sra'):        os.system('fastq-dump --gzip --split-3 ' + item)for item in os.listdir('./'):    if item.endswith('_1.fastq.gz'):        sra_accession = item[:9]        out_file = sra_accession + '_kal_out'        os.system('kallisto quant -i ' + index_file + ' -o ' + out_file +                  ' --single -l 200 -s 20 ./' + sra_accession + '_1.fastq.gz')